    val titlePadding : Option[CornerRadius] = None,
    val titleX : Option[CornerRadius] = None,
    val titleY : Option[CornerRadius] = None,
    val translate : Option[CornerRadius] = None,

    /**
     * Explicitly set the visible axis tick values.
